/**
 * Dispatches any actions related to the lookup state.
 *
 * @param state - The current lookup state
 * @param action - The action to dispatch
 *
 * @returns The updated lookup state
 */
export default function (state: LookupState = initialState, action: any): LookupState {
    switch (action.type) {
        case LOOKUP_LOADING:
        case UNITS_LOADING:
            return {
                ...state,
                isLoading: true
            }
        case LOOKUP_LOADED:
            return {
                ...state,
                isLoading: false,
                lookupValues: action.payload
            }
        case UNITS_LOADED:
            return {
                ...state,
                isLoading: false,
                unitValues: action.payload
            }
        case LOOKUP_FAILED:
            return {
                ...state,
                isLoading: false,
                lookupValues: {
                    EinheitlicheWaermeversorgung: [],
                    GWHArt: [],
                    Bedachungsmaterial: [],
                    Stehwandmaterial: [],
                    AlterdesBedachungsmaterials: [],
                    Energieschirm: [],
                    Transportsystem: [],
                    Produktionstyp: [],
                    Kultursystem: [],
                    AnzahlTriebe: [],
                    ZusaetzlichesHeizsystem: [],
                    "10-30Gramm(Snack)": [],
                    "30-100Gramm(Cocktail)": [],
                    "100-150Gramm(Rispen)": [],
                    ">150Gramm(Fleisch)": [],
                    Nebenkultur: [],
                    Entfeuchtung: [],
                    Energietraeger: [],
                    BHKW: [],
                    Stromherkunft: [],
                    Zusatzbelichtung: [],
                    Belichtungsstrom: [],
                    "CO2-Herkunft": [],
                    "Duengemittel:VereinfachteAngabe": [],
                    "Duengemittel:DetaillierteAngabe": [],
                    Nuetzlinge: [],
                    GrowbagsKuebel: [],
                    Substrat: [],
                    "SchnuereRankhilfen:Material": [],
                    Klipse: [],
                    "Klipse:Material": [],
                    Rispenbuegel: [],
                    "Rispenbuegel:Material": [],
                    Bewaesserungsart: [],
                    Bodenabdeckung: [],
                    "Jungpflanzen:Zukauf": [],
                    "Jungpflanzen:Substrat": [],
                    Verpackungsmaterial: [],
                    SonstigeVerbrauchsmaterialien: [],
                    ZusaetzlicherMaschineneinsatz: [],
                    BelichtungsstromEinheit: [],
                }
            }
        case UNITS_FAILED:
        case RESET_DATA:
            return initialState
        default:
            return state
    }
}